mapper.select:
select *,
(select u.user_level from `user` u where u.user_id=r.target_id) as level
 from review r where user_id = #{user_id}

select *,
(select u.name from user u where u.user_id = r.user_id) as user_name
 from review r where target_id = #{trainer_id}

select *,
(select u.name from user u where u.user_id = r.user_id) as user_name
 from review r where target_id = #{center_id}
