mapper.insert:
insert into popup (
    user_id, title, start_date, end_date,
    position_top, position_left, position_right, position_bottom,
    popup_width, popup_height, use_toggle, file_name
)
values (
    #{user_id}, #{title}, #{start_date}, #{end_date},
    #{position_top}, #{position_left}, #{position_right}, #{position_bottom},
    #{popup_width}, #{popup_height}, #{use_toggle}, #{file_name}
)

mapper.update:
UPDATE popup
SET use_toggle = NOT use_toggle
WHERE popup_idx = #{popup_idx}

