mapper.update:
UPDATE product SET 
    product_name = #{dto.product_name},
    price = #{dto.price},
    discount_rate = #{dto.discount_rate},
    max_people = #{dto.max_people},
    count = #{dto.count},
    expiration_period = #{dto.expiration_period}
WHERE center_id = #{user_id}
  AND product_idx = #{product_idx}
