mapper.select:
select cp.center_name, concat(cp.sido,' ', cp.gugun,' ', cp.eupmyeondong,' ', cp.detail_address) as address , (select min(p.price) from product p where p.center_id=cp.center_id)as price, (select avg(r.rating) from review r where r.target_id=cp.center_id)as rating, (select group_concat(t.tag_name) from user_tag ut join tag t on ut.tag_idx=t.tag_idx where ut.user_id=cp.center_id)as tags, (select file_name from profile_file pf where pf.user_id=cp.center_id) as file_name from center_profile cp where cp.sido = #{sido} and cp.gugun = #{gugun} and cp.eupmyeondong = #{eupmyeondong}
and cp.exercise = #{exercise}
and exists( select 1 from user_tag ut join tag t on ut.tag_idx = t.tag_idx join user u on ut.user_id = u.user_id where t.tag_name IN
#{tag}
)
order by rating desc
order by price asc
order by cp.center_id desc

select cp.center_name , cp.exercise , cp.latitude , cp.longitude , (select pf.file_name from profile_file pf where pf.user_id=tp.user_id) as file_name, (select avg(r.rating) from review r where r.target_id = tp.user_id) as rating , (select min(p.price) from product p where p.trainer_id=tp.user_id) as price, (select name from `user` u where u.user_id=tp.user_id) as name, (select group_concat(t.tag_name) from user_tag ut join tag t on ut.tag_idx=t.tag_idx where ut.user_id=tp.user_id) as tags from trainer_profile tp join center_profile cp on cp.center_idx = tp.center_idx join user u on tp.user_id = u.user_id where cp.sido = #{sido} and cp.gugun = #{gugun} and cp.eupmyeondong = #{eupmyeondong}
and cp.exercise = #{exercise}
and u.gender = #{gender}
and (select avg(r.rating) from review r where r.target_id = tp.user_id) >= #{minRating}
and exists( select 1 from user_tag ut join tag t on ut.tag_idx = t.tag_idx join user u on ut.user_id = u.user_id where t.tag_name IN
#{tag}
)
order by price asc
order by rating desc
